#include <iostream>\n#include <limits>\n\nbool lowestPositive(int u0, int u1, int& result) {\n    // Check if both numbers are non-positive\n    if (u0 <= 0 && u1 <= 0) {\n        return false; // No positive number found\n    }\n    \n    // Use ternary operator to find the smallest positive number\n    result = (u0 > 0 && u1 > 0) ? std::min(u0, u1) : \n             (u0 > 0) ? u0 : u1;\n    return true;\n}\n\nint main() {\n    int result;\n    if (lowestPositive(5, 6, result)) {\n        std::cout << "The smallest positive number is: " << result << std::endl;\n    } else {\n        std::cout << "No positive number found." << std::endl;\n    }\n    return 0;\n}