#include <iostream>\n#include <vector>\nusing namespace std;\n\npair<char, int> findCandidate(const vector<char>& arr) {\n    char majority = ' ';\n    int count = 0;\n    \n    for (char num : arr) {\n        if (count == 0) {\n            majority = num;\n            count = 1;\n        } else if (num == majority) {\n            count++;\n        } else {\n            count--;\n        }\n    }\n    return {majority, count};\n}\n\nbool isMajority(const vector<char>& arr, char candidate) {\n    int count = 0;\n    for (char num : arr) {\n        if (num == candidate) {\n            count++;\n        }\n    }\n    return count > arr.size() / 2;\n}\n\nint main() {\n    vector<char> arr = {'A', 'A', 'A', 'C', 'C', 'B', 'B'};\n    \n    auto [candidate, _] = findCandidate(arr);\n    if (isMajority(arr, candidate)) {\n        cout << "The majority element is: " << candidate << endl;\n    } else {\n        cout << "There is no majority element." << endl;\n    }\n    \n    return 0;\n}