#include <vcclr.h>\n#using <System.dll>\n#using <AngleSharp.dll>\n\nusing namespace System;\nusing namespace AngleSharp;\nusing namespace AngleSharp::Html;\n\nint main(array<System::String ^> ^args)\n{\n    // Create a new configuration to be used by the context\n    auto config = Configuration::Default();\n\n    // Create an instance of IBrowsingContext\n    auto browsingContext = BrowsingContext::New(config);\n\n    // Define an HTML document\n    String^ content = "<html><body><h1>Hello World</h1></body></html>";\n\n    // Parse the document from a string\n    auto documentTask = browsingContext->OpenAsync(content);\n    auto document = documentTask->GetAwaiter()->GetResult();\n\n    // Output the title of the document (result: Hello World)\n    Console::WriteLine(document->DocumentElement->QuerySelector("h1")->InnerText);\n\n    return 0;\n}