// In MyDll.h\n#ifdef MYDLL_EXPORTS\n#define MYDLL_API __declspec(dllexport)\n#else\n#define MYDLL_API __declspec(dllimport)\n#endif\n\nclass MYDLL_API MyClass {\npublic:\n    virtual void myVirtualFunction(); // Exported because of 'virtual'\n    \n    inline void myInlineFunction() { // Inline function definition in header\n        // Function implementation\n    }\n\n    void nonVirtualFunction(); // Non-virtual function\n};\n\nMYDLL_API MyClass* createInstance(); // Factory function to create instances\n\n// In MyDll.cpp (implementation file)\n#include "MyDll.h"\n\nvoid MyClass::myVirtualFunction() {\n    // Implementation of virtual function\n}\n\nvoid MyClass::nonVirtualFunction() { // Non-inline, non-virtual function\n    // Implementation details\n}\n\nMYDLL_API MyClass* createInstance() {\n    return new MyClass();\n}