#include <windows.h>\n#include "base/Sprite.h"\n\ntypedef base::Sprite* (*CreateSpriteFunc)(base::Texture*);\ntypedef void (*DestroySpriteFunc)(base::Sprite*);\n\nint main() {\n    HMODULE hModule = LoadLibrary("Direct3DSprite.dll");\n    if (!hModule) return -1;\n\n    CreateSpriteFunc createSprite = (CreateSpriteFunc)GetProcAddress(hModule, "CreateSprite");\n    DestroySpriteFunc destroySprite = (DestroySpriteFunc)GetProcAddress(hModule, "DestroySprite");\n\n    if (!createSprite || !destroySprite) {\n        FreeLibrary(hModule);\n        return -2;\n    }\n\n    // Create and use your sprite\n    base::Texture* texture = // obtain a texture\n    base::Sprite* sprite = createSprite(texture);\n\n    // Use sprite...\n\n    destroySprite(sprite);\n    FreeLibrary(hModule);\n\n    return 0;\n}