#include <afxwin.h>\n\n// Assume IDD_MYDIALOG is defined in your .rc file within the DLL project\nextern "C" __declspec(dllexport) void ShowDialog(CWnd* pParent)\n{\n    // Save the old resource handle\n    HINSTANCE hPrev = AfxGetResourceHandle();\n    \n    // Set the current module's resource handle to this DLL's\n    AfxSetResourceHandle(AfxGetInstanceHandle());\n\n    CDialogEx dlg(IDD_MYDIALOG, pParent);\n    if (dlg.DoModal() == IDOK) {\n        // Handle OK button click or any other dialog logic here\n    }\n\n    // Restore the previous resource handle\n    AfxSetResourceHandle(hPrev);\n}