#include <afxdisp.h> // For COleDispatchDriver\n\n// Assuming IS8SimObject has a method CountContents that returns VT_I4 (long)\nclass CS8SimObject : public COleDispatchDriver\n{\npublic:\n    CS8SimObject() {}\n    CS8SimObject(LPDISPATCH pDispatch) { AttachDispatch(pDispatch); }\n    CS8SimObject(const CS8SimObject& dispatchSrc) { AttachDispatch(dispatchSrc.GetIDispatch(FALSE)); }\n\n    long CountContents()\n    {\n        if (m_pDispatch == NULL)\n            return 0;\n\n        COleVariant result;\n        DISPPARAMS dp = {NULL, NULL, 0, 0};\n\n        HRESULT hr = m_pDispatch->Invoke(\n            DISPID_COUNTCONTENTS,\n            IID_NULL,\n            LOCALE_USER_DEFAULT,\n            DISPATCH_METHOD | DISPATCH_PROPERTYGET,\n            &dp,\n            &result,\n            NULL,\n            NULL);\n\n        if (SUCCEEDED(hr))\n            return static_cast<long>(result.lVal);\n        else\n            throw COleDispatchException(hr);\n    }\n};\n\nvoid ExampleFunction()\n{\n    // Assuming pis8->GetSimObject returns a valid COM object for CS8SimObject.\n    _variant_t resultsDataString = pis8->GetSimObject(_bstr_t(L"TEST example 3"));\n    \n    if (resultsDataString.vt == VT_DISPATCH)\n    {\n        CS8SimObject simObject(resultsDataString.pdispVal);\n        \n        long countContents1 = simObject.CountContents();\n        \n        // Assuming you have another similar object named "Example 2"\n        _variant_t secondResult = pis8->GetSimObject(_bstr_t(L"Example 2"));\n        CS8SimObject simObject2(secondResult.pdispVal);\n        long countContents2 = simObject2.CountContents();\n\n        CString resultString;\n        resultString.Format(L"%ld*", countContents1 + countContents2);\n\n        // Do something with the result string\n        AfxMessageBox(resultString);\n    }\n}