#include <iostream>\n#include <string>\n\nstd::string concatenateWithReserve(const std::vector<std::string>& strings) {\n    size_t totalLength = 0;\n    for (const auto& str : strings) {\n        totalLength += str.length();\n    }\n\n    std::string result;\n    result.reserve(totalLength); // Reserve enough space to avoid multiple allocations\n\n    for (const auto& str : strings) {\n        result += str; // Efficient concatenation\n    }\n    return result;\n}\n\nint main() {\n    std::vector<std::string> strings = {"Hello", " ", "World", "!"};\n    std::cout << concatenateWithReserve(strings) << std::endl;\n    return 0;\n}