#include <iostream>\n#include <unistd.h>\n#include <sys/types.h>\n#include <sys/wait.h>\n\nint shared;\n\nvoid func(){\n    for (int i=0; i<10;i++)\n        shared++;\n    std::cout << "Process " << getpid() << ", shared "\n              << shared << ", &shared "\n              << &shared << std::endl;\n}\n\nint main(){\n    pid_t p1, p2;\n    int status;\n    shared = 0;\n\n    if ((p1=fork())==0) { func(); exit(0); }\n    if ((p2=fork())==0) { func(); exit(0); }\n\n    for(int i=0;i<10;i++)\n        shared++;\n\n    waitpid(p1, &status, 0);\n    waitpid(p2, &status, 0);\n\n    std::cout << "shared variable is: " << shared << std::endl;\n    std::cout << "Process " << getpid() << ", shared "\n              << shared << ", &shared "\n              << &shared << std::endl;\n}