#include <iostream>\n#include <fstream>\n#include <sstream>\n#include <vector>\n\nstd::vector<std::vector<std::string>> readTabDelimitedFile(const std::string& filename) {\n    std::ifstream file(filename);\n    std::vector<std::vector<std::string>> data;\n    \n    if (!file.is_open()) {\n        throw std::runtime_error("Could not open file");\n    }\n    \n    std::string line;\n    while (std::getline(file, line)) {\n        std::istringstream iss(line);\n        std::vector<std::string> record;\n        std::string field;\n        while (std::getline(iss, field, '\t')) { // use tab as delimiter\n            record.push_back(field);\n        }\n        data.push_back(record);\n    }\n    \n    file.close();\n    return data;\n}\n\nint main() {\n    try {\n        auto records = readTabDelimitedFile("data.txt");\n        for (const auto& record : records) {\n            for (const auto& field : record) {\n                std::cout << "[" << field << "] ";\n            }\n            std::cout << std::endl;\n        }\n    } catch (const std::exception& e) {\n        std::cerr << "Error: " << e.what() << std::endl;\n    }\n    \n    return 0;\n}