#include <atomic>\n#include <thread>\n\nstd::atomic<bool> dataUpdated(false);\n\nvoid thread1Function() {\n    while(1) {\n        if (dataUpdated.load()) { // Use .load() to read the value atomically\n            updateScreen();\n        }\n        doSomethingElse();\n    }\n}\n\nvoid thread2Function() {\n    while(1) {\n        if (doSomething())\n            dataUpdated.store(true); // Use .store() to write the value atomically\n    }\n}