#include "interface.h"\n#include "icliententitylist.h"\n\nvoid PrintPlayerCount(const CCommand &args)\n{\n    IClientEntityList *entList = Interfaces::GetClientEntityList();\n    int activePlayers = 0;\n    for (int i = 1; i <= entList->GetHighestIndex(); ++i)\n    {\n       IClientNetworkable *networkable = entList->GetClientNetworkable(i);\n        if (!networkable)\n            continue;\n\n        IClientEntity* entity = networkable->GetIClientEntity();\n        if (!entity)\n            continue;\n\n        // Check the class ID to see if it's a player; this is just an example and may not work as expected\n        ClientClass *clientClass = networkable->GetClientClass();\n        if (clientClass && !Q_strcmp(clientClass->m_pNetworkName, "CBasePlayer"))\n            activePlayers++;\n    }\n    ConMsg("Active players: %d\n", activePlayers);\n}\n\nstatic ConCommand countplayers("countplayers", PrintPlayerCount, "Counts the number of active players.", FCVAR_DONTRECORD);