#include <windows.h>\n#include <iostream>\n\nint main() {\n    DWORD bufferLength = 0;\n    GetLogicalProcessorInformation(nullptr, &bufferLength);\n    PSYSTEM_LOGICAL_PROCESSOR_INFORMATION buffer = (PSYSTEM_LOGICAL_PROCESSOR_INFORMATION)malloc(bufferLength);\n\n    if (!GetLogicalProcessorInformation(buffer, &bufferLength)) {\n        std::cerr << "Failed to get logical processor information." << std::endl;\n        free(buffer);\n        return 1;\n    }\n\n    int cacheLineSize = -1;\n    PSYSTEM_LOGICAL_PROCESSOR_INFORMATION ptr = buffer;\n\n    while (bufferLength > 0) {\n        if (ptr->Relationship == RelationCache && \n            ptr->Cache.Level == 1 &&\n            ptr->Cache.Type == CacheData) { // or CacheUnified for unified caches\n            cacheLineSize = ptr->Cache.LineSize;\n            break;\n        }\n        ptr++;\n        bufferLength -= sizeof(SYSTEM_LOGICAL_PROCESSOR_INFORMATION);\n    }\n\n    free(buffer);\n\n    if (cacheLineSize != -1) {\n        std::cout << "Cache Line Size: " << cacheLineSize << " bytes" << std::endl;\n    } else {\n        std::cerr << "Failed to retrieve cache line size." << std::endl;\n    }\n    return 0;\n}