#include <iostream>\n#include <sstream>\n#include <string>\n\nstd::string concatenateWithOStringStream(const std::vector<std::string>& strings) {\n    std::ostringstream oss;\n    for (const auto& str : strings) {\n        oss << str;\n    }\n    return oss.str();\n}\n\nint main() {\n    std::vector<std::string> strings = {"Hello", " ", "World", "!"};\n    std::cout << concatenateWithOStringStream(strings) << std::endl;\n    return 0;\n}