#include <iostream>\n#include <map>\n#include <string>\n\nclass MyClass {\npublic:\n    static int dude;\n};\n\nint MyClass::dude = 42;\n\nstd::map<std::string, void*> variableMap = {{"MyClass::dude", &MyClass::dude}};\n\nvoid modifyVariable(const std::string& varName, const std::string& newValue) {\n    if (varName == "MyClass::dude") {\n        *static_cast<int*>(variableMap[varName]) = std::stoi(newValue);\n    }\n}\n\nint main() {\n    std::cout << "Initial value: " << MyClass::dude << std::endl;\n    modifyVariable("MyClass::dude", "100");\n    std::cout << "Modified value: " << MyClass::dude << std::endl;\n    return 0;\n}