#include <iostream>\n#include <thread>\n\n// Function used by threads\nvoid threadFunction() {\n    std::cout << "Thread is running.\n";\n}\n\nint main() {\n    // Creating a new process would typically involve forking (in UNIX-based systems)\n    // Here we simply create multiple threads\n    std::thread t1(threadFunction);\n    std::thread t2(threadFunction);\n\n    // Wait for the threads to finish\n    t1.join();\n    t2.join();\n\n    std::cout << "Both threads are done.\n";\n\n    return 0;\n}