#include <iostream>\n#include <fstream>\n\nint main(){\n    struct student\n    {\n        int rollNo;\n    };\n    struct student stud1;\n    stud1.rollNo = 1;\n\n    std::ofstream fout;\n    fout.open("stu1.dat", std::ios::binary);\n    if (!fout) {\n        std::cerr << "Error opening output file!" << std::endl;\n        return 1;\n    }\n    fout.write(reinterpret_cast<char*>(&stud1), sizeof(stud1));\n    fout.close();\n\n    std::ifstream fin("stu1.dat", std::ios::binary);\n    if (!fin) {\n        std::cerr << "Error opening input file!" << std::endl;\n        return 1;\n    }\n\n    struct student tmpStu;\n    while (fin.read(reinterpret_cast<char*>(&tmpStu), sizeof(tmpStu))) {\n        std::cout << tmpStu.rollNo << std::endl; \n    }\n    fin.close();\n\n    return 0;\n}