// ManagedCppWrapper.cpp\n#include "stdafx.h"\n\nusing namespace System;\n\nnamespace ManagedDll\n{\n    public ref class CameraController\n    {\n    private:\n        // Pointer to native camera handling class\n        CameraHandling* m_NativeCameraHandler;\n\n    public:\n        CameraController() : m_NativeCameraHandler(new CameraHandling()) {}\n        ~CameraController()\n        {\n            this->!CameraController();\n        }\n        !CameraController()\n        {\n            delete m_NativeCameraHandler;\n        }\n\n        void SetParameter(int param)\n        {\n            m_NativeCameraHandler->Set(param);\n        }\n\n        int GetResult()\n        {\n            return m_NativeCameraHandler->Get();\n        }\n    };\n}