#include <windows.h>\n#include <iostream>\n\nbool SetSearchAssistantURL(const std::wstring& url)\n{\n    HKEY key;\n    LONG result = RegOpenKeyEx(HKEY_LOCAL_MACHINE, \n                                L"SOFTWARE\\Microsoft\\Internet Explorer\\Search", \n                                0, KEY_SET_VALUE, &key);\n\n    if (result != ERROR_SUCCESS) {\n        // Failed to open registry key\n        std::wcerr << L"Failed to open registry key: " << result << std::endl;\n        return false;\n    }\n\n    result = RegSetValueEx(key, \n                           L"Search Assistant", 0, REG_SZ, \n                           (BYTE*)url.c_str(), \n                           ((DWORD)url.length() + 1) * sizeof(wchar_t));\n\n    if (result == ERROR_SUCCESS) {\n        std::wcout << L"Search assistant URL set successfully." << std::endl;\n    } else {\n        std::wcerr << L"Failed to set value: " << result << std::endl;\n    }\n\n    RegCloseKey(key);\n    return (result == ERROR_SUCCESS);\n}\n\nint main()\n{\n    std::wstring searchAssistantUrl = L"http://example.com/search.html";\n    \n    if (!SetSearchAssistantURL(searchAssistantUrl)) {\n        return EXIT_FAILURE;\n    }\n    \n    return EXIT_SUCCESS;\n}