#include <tnt/tntnet.h>\n#include <tnt/httprequest.h>\n#include <tnt/httpreply.h>\n\nclass HelloWorldComponent : public tnt::Component {\npublic:\n    void run(tnt::HttpRequest& request, tnt::HttpReply& reply) override\n    {\n        reply.out() << "Hello World!";\n    }\n};\n\nint main(int argc, char* argv[])\n{\n    tnt::Tntnet tnt;\n    tnt.mapUrl("^/$", std::make_shared<HelloWorldComponent>());\n    return tnt.run(argc, argv);\n}