#include "Poco/Net/HTTPClientSession.h"\n#include "Poco/Net/HTTPRequest.h"\n#include "Poco/Net/HTTPResponse.h"\n#include <iostream>\n#include <sstream>\n\nusing namespace Poco::Net;\nusing namespace std;\n\nint main() {\n    try {\n        // Create a session object with the host name and port number\n        HTTPClientSession session("example.com", 80);\n\n        // Prepare the request to send\n        HTTPRequest request(HTTPRequest::HTTP_GET, "/", HTTPMessage::HTTP_1_1);\n        \n        // Send the request\n        session.sendRequest(request);\n\n        // Get response\n        HTTPResponse response;\n        istream& rs = session.receiveResponse(response);\n\n        // Print status and reason for the response\n        cout << response.getStatus() << " " << response.getReason() << endl;\n\n        // Read body\n        StreamCopier::copyStream(rs, cout);\n    } catch (Exception& ex) {\n        cerr << ex.displayText() << endl;\n        return 1;\n    }\n    return 0;\n}