#include <windows.h>\n#include <iostream>\n#include <string>\n\nvoid PrintFileVersion(const std::wstring& filePath) {\n    DWORD dwHandle = 0;\n    DWORD dwLen = GetFileVersionInfoSize(filePath.c_str(), &dwHandle);\n\n    if (dwLen == 0) {\n        std::wcout << L"Error: Unable to get file version info size." << std::endl;\n        return;\n    }\n\n    BYTE* lpBuffer = new BYTE[dwLen];\n    if (!GetFileVersionInfo(filePath.c_str(), dwHandle, dwLen, lpBuffer)) {\n        std::wcout << L"Error: Unable to get file version information." << std::endl;\n        delete[] lpBuffer;\n        return;\n    }\n\n    VS_FIXEDFILEINFO* pVsffi = NULL;\n    UINT uLen;\n\n    if (!VerQueryValue(lpBuffer, L"\\", (LPVOID*)&pVsffi, &uLen)) {\n        std::wcout << L"Error: Unable to query file version value." << std::endl;\n        delete[] lpBuffer;\n        return;\n    }\n\n    DWORD dwFileVersionMS = pVsffi->dwFileVersionMS;\n    DWORD dwFileVersionLS = pVsffi->dwFileVersionLS;\n\n    std::wcout << L"File Version: " \n               << HIWORD(dwFileVersionMS) << L"."\n               << LOWORD(dwFileVersionMS) << L"."\n               << HIWORD(dwFileVersionLS) << L"."\n               << LOWORD(dwFileVersionLS) << std::endl;\n\n    delete[] lpBuffer;\n}\n\nint main() {\n    std::wstring dllPath = L"C:\\path\\to\\your\\dllfile.dll";\n    PrintFileVersion(dllPath);\n    return 0;\n}