#include <iostream>\n#include <vector>\n#include <fstream>\n#include <sstream>\n#include <ctime>\nusing namespace std;\n\nint main() {\n    // Initialize Random Seed \n    srand(time(NULL));\n\n    double SubsRate = 0.003;\n    double nofRuns   = 1000000;\n    int countSubstitutions = 0;\n\n    for (unsigned i = 0; i < nofRuns; i++) {\n        // Generate a random number between 0 and 999\n        int toSub = rand() % 1000 + 1;\n\n        // Check if the random number falls within the substitution rate range\n        if (toSub <= SubsRate * 1000) {\n            countSubstitutions++;\n        }\n    }\n\n    cout << "Total substitutions: " << countSubstitutions << endl;\n    return 0;\n}