#include <iostream>\n#include <list>\n\n#define NUM_VERTICES 8\n\nusing namespace std;\n\nenum { WHITE, GRAY, BLACK };\n\nstruct vertex {\n    int color;\n    int distance;\n    char parent;\n};\n\nint main() {\n    // Create the vertices\n    vertex r = {WHITE, 0, '\0'}; // Note: NULL is typically used for pointers, not integers or chars\n\n    // Create pointer to the vertex structures\n    vertex *pr = &r;\n\n    // Create a list to hold the vertices\n    list<vertex*> r_list; // No need to use new here unless you specifically need dynamic allocation\n\n    // Use push_back instead of insert if you want to add elements at the end\n    r_list.push_back(pr);\n\n    // Alternatively, if you still want to use insert\n    // Inserting at the beginning of the list\n    r_list.insert(r_list.begin(), pr); \n\n    return 0;\n}