// Messages.h\n#ifndef MESSAGES_H\n#define MESSAGES_H\n\nclass Messages {\npublic:\n    static const std::string& getMessage1();\n    static const std::string& getMessage2();\n    static const std::string& getMessage3();\n\nprivate:\n    static const std::string msg1;\n    static const std::string msg2;\n    static const std::string msg3;\n};\n\n#endif // MESSAGES_H\n\n// Messages.cpp\n#include "Messages.h"\n\nconst std::string Messages::msg1 = "message1";\nconst std::string Messages::msg2 = "message2";\nconst std::string Messages::msg3 = "message3";\n\nconst std::string& Messages::getMessage1() { return msg1; }\nconst std::string& Messages::getMessage2() { return msg2; }\nconst std::string& Messages::getMessage3() { return msg3; }\n\n// Usage in SomeClass\nvoid SomeClass::function1() {\n    // Access messages using static methods from another class\n    const std::string& msg = Messages::getMessage1();\n}