#include <iostream>\n#include <comdef.h>\n\nint main() {\n    // Initialize the COM library for use by the calling thread.\n    HRESULT hr = CoInitializeEx(0, COINIT_APARTMENTTHREADED);\n    \n    if (SUCCEEDED(hr)) {\n        std::cout << "COM initialized with Single Threaded Apartment." << std::endl;\n        \n        // Perform COM operations here...\n\n        // Uninitialize the COM library for this thread.\n        CoUninitialize();\n    } else {\n        _com_error err(hr);\n        std::cerr << "COM initialization failed: " << err.ErrorMessage() << std::endl;\n    }\n\n    return 0;\n}