#include <unrar.h>\n\nint main() {\n    // Example pseudo-code for opening an archive and extracting its contents\n    UNRAR::Archive* archive = UNRAR::Open("example.rar");\n    if (archive == nullptr) {\n        return 1; // Error opening the file\n    }\n    \n    // Extract files to a directory\n    bool success = archive->ExtractTo("output_directory/");\n    \n    delete archive;\n    return success ? 0 : 1;\n}