#include <iostream>\n#include <fstream>\n#include <string>\n\nvoid readLinesFromFile(const std::string& filename, int startLine, int endLine) {\n    std::ifstream file(filename);\n    if (!file.is_open()) {\n        std::cerr << "Could not open the file - '" << filename << "'" << std::endl;\n        return;\n    }\n\n    std::string line;\n    int currentLine = 0;\n\n    while (std::getline(file, line)) {\n        ++currentLine;\n        if (currentLine < startLine) continue;\n        if (currentLine > endLine) break;\n        \n        // Process the line\n        std::cout << line << std::endl;\n    }\n\n    file.close();\n}\n\nint main() {\n    int startX = 5; // Start from line 5\n    int endY = 10;  // End at line 10\n    readLinesFromFile("example.txt", startX, endY);\n    return 0;\n}