#include <comdef.h>\n#include <iostream>\n\nint main() {\n    CoInitialize(NULL); // Initialize COM library\n\n    CLSID clsid;\n    HRESULT hr = CLSIDFromProgID(L"MyComponent.MyObject", &clsid);\n    if (FAILED(hr)) {\n        std::cerr << "Failed to get CLSID" << std::endl;\n        return 1;\n    }\n\n    LPUNKNOWN pUnknown = NULL;\n    hr = CoCreateInstance(clsid, NULL, CLSCTX_INPROC_SERVER, IID_IUnknown, (LPVOID*)&pUnknown);\n    if (FAILED(hr)) {\n        std::cerr << "Failed to create an instance" << std::endl;\n        CoUninitialize();\n        return 1;\n    }\n\n    // Use the COM object here\n\n    pUnknown->Release(); // Release the COM object\n    CoUninitialize(); // Uninitialize COM library\n    return 0;\n}