#include <windows.h>\n#include <winnetwk.h>\n#include <iostream>\n\nvoid DisconnectNetworkDrive(const char* driveLetter) {\n    DWORD result = WNetCancelConnection2A(driveLetter, 0, TRUE);\n    \n    if (result == NO_ERROR) {\n        std::cout << "Successfully disconnected " << driveLetter << "\n";\n    } else {\n        std::cerr << "Failed to disconnect " << driveLetter << ". Error: " << result << "\n";\n    }\n}\n\nint main() {\n    // Disconnect the Z: network drive\n    DisconnectNetworkDrive("Z:");\n    return 0;\n}