#include <tesseract/baseapi.h>\n#include <leptonica/allheaders.h>\n\nint main() {\n    char *outText;\n\n    tesseract::TessBaseAPI api;\n    // Initialize tesseract-ocr with English, without specifying tessdata path explicitly:\n    if (api.Init(NULL, "eng")) {\n        fprintf(stderr, "Could not initialize tesseract.\n");\n        exit(1);\n    }\n\n    // Open input image with leptonica library\n    Pix *image = pixRead("path_to_your_image.png");\n    api.SetImage(image);\n\n    // Get OCR result\n    outText = api.GetUTF8Text();\n    printf("OCR output:\n%s", outText);\n\n    // Clean up used resources and memory.\n    api.End();\n    pixDestroy(&image);\n    delete[] outText;\n\n    return 0;\n}