#include <list>\n#include <queue>\n#include <cstdint>\n\nstruct Node {\n    uint32_t ID;\n    char * Value;\n};\n\nclass myClass {\nprivate:\n    uint32_t myValueLength;\n\npublic:\n    std::list<Node *> MyQueue;\n\n    bool compare(Node * first, Node * second);\n\n    bool doStuff();\n};\n\nbool myClass::compare(Node * first, Node * second) {\n    unsigned int ii = 0;\n    while (ii < myValueLength) {\n        if (first->Value[ii] < second->Value[ii]) {\n            return true;\n        } else if (first->Value[ii] > second->Value[ii]) {\n            return false;\n        }\n        ++ii;\n    }\n    return false;\n}\n\nbool myClass::doStuff() {\n    MyQueue.sort([this](Node* first, Node* second) {\n        return this->compare(first, second);\n    });\n    return true;\n}