#include <windows.h>\n#include <iostream>\n\nint main() {\n    HRESULT hr = 0x80004027; // Example HRESULT value\n    wchar_t* sErrMsg;\n\n    if (FormatMessage(FORMAT_MESSAGE_ALLOCATE_BUFFER | FORMAT_MESSAGE_FROM_SYSTEM |\n        FORMAT_MESSAGE_IGNORE_INSERTS,\n        NULL, hr, MAKELANGID(LANG_NEUTRAL, SUBLANG_DEFAULT),\n        (LPTSTR)&sErrMsg, 0, NULL) == 0) {\n        std::cout << "Error in FormatMessage.";\n    }\n    else {\n        std::wcerr << L"Error code 0x" << std::hex << hr << L": " << sErrMsg;\n        LocalFree(sErrMsg);\n    }\n\n    return 0;\n}