#include <iostream>\n#include <string>\n\nint main() {\n    std::string line;\n\n    while (std::cout << ">>> " && std::getline(std::cin, line)) {\n        if (!line.empty()) {\n            // Process the input line here\n            std::cout << "You entered: " << line << std::endl;\n        }\n    }\n\n    if (std::cin.eof()) {\n        std::cout << "\nEOF encountered. Exiting shell." << std::endl;\n    } else if (std::cin.fail() && !std::cin.eof()) {\n        std::cerr << "\nInput error occurred." << std::endl;\n    }\n\n    return 0;\n}