#include <iostream>\n#include <fstream>\n#include <string>\n\nint main() {\n    std::ifstream file("example.txt"); // Open the file\n\n    if (!file.is_open()) {\n        std::cerr << "Failed to open file!" << std::endl;\n        return 1;\n    }\n\n    std::string line;\n    while (getline(file, line)) { // Read each line\n        std::cout << line << std::endl; // Output the line\n    }\n\n    file.close(); // Close the file\n    return 0;\n}