#include <windows.h>\n#include <stdio.h>\n\nbool IsLaunchedFromExplorer() {\n    // Get the handle for standard input.\n    HANDLE stdinHandle = GetStdHandle(STD_INPUT_HANDLE);\n    \n    // Check if the standard input is a console.\n    return (stdinHandle != INVALID_HANDLE_VALUE && \n            !GetConsoleMode(stdinHandle, NULL));\n}\n\nint main(int argc, char* argv[]) {\n    // Example usage of the function\n    if (argc < 2) { // Assuming you need at least one parameter\n        fprintf(stderr, "Error: Missing parameters.\n");\n        \n        // If launched from explorer, wait for a key press before exiting.\n        if (IsLaunchedFromExplorer()) {\n            printf("Press any key to exit...");\n            getchar();\n        }\n    } else {\n        // Process the arguments and do your work here\n        printf("Parameters provided:\n");\n        for (int i = 0; i < argc; ++i) {\n            printf("%s\n", argv[i]);\n        }\n    }\n\n    return 0;\n}