#include <windows.h>\n#include <iostream>\n\ntypedef int (*MyFunction)();\n\nint main() {\n    HMODULE hModule = LoadLibrary(TEXT("YourDll.dll"));\n    if (hModule != NULL) {\n        MyFunction myFunction = (MyFunction)GetProcAddress(hModule, "YourExportedFunctionName");\n        if (myFunction != NULL) {\n            int result = myFunction();\n            std::cout << "Result: " << result << std::endl;\n        } else {\n            std::cerr << "Failed to find function" << std::endl;\n        }\n        FreeLibrary(hModule);\n    } else {\n        std::cerr << "Failed to load DLL" << std::endl;\n    }\n\n    return 0;\n}