// Example using CGAL for 2D surface mesh generation from a set of points\n  #include <CGAL/Exact_predicates_inexact_constructions_kernel.h>\n  #include <CGAL/Surface_mesh_default_triangulation_3.h>\n  #include <CGAL/make_surface_mesh.h>\n  #include <CGAL/Implicit_to_labeling_function_wrapper.h>\n\n  typedef CGAL::Exact_predicates_inexact_constructions_kernel K;\n  typedef CGAL::Surface_mesh_default_triangulation_3 Tr;\n\n  int main() {\n    // Define an implicit function\n    auto f = [](const K::Point_3& point) -> FT {\n      return CGAL::sqrt(point.x()*point.x()+point.y()*point.y()) - 1.0;\n    };\n\n    Tr tr; // Empty mesh\n    CGAL::make_surface_mesh(tr, \n                            CGAL::Implicit_surface_3<K>(f, K::Sphere_3(CGAL::ORIGIN, 4.)),\n                            CGAL::make_triangulation_updater(tr));\n\n    return 0;\n  }