#include <iostream>\n#include <iomanip>\n#include <sstream>\n\nstd::string leftPad(const std::string& str, size_t width) {\n    if (str.size() >= width) {\n        return str;\n    }\n    std::ostringstream os;\n    os << std::setw(width) << std::left << str;\n    return os.str();\n}\n\nint main() {\n    std::string input = "123";\n    size_t width = 10;\n    std::string paddedString = leftPad(input, width);\n    std::cout << "[" << paddedString << "]" << std::endl; // Use brackets to visualize padding\n    return 0;\n}