#include <iostream>\n\nvoid clearConsole() {\n#ifdef _WIN32 // Check if the platform is Windows\n    std::system("cls");\n#elif __linux__ || __APPLE__ // Check if the platform is Linux or MacOS\n    std::system("clear");\n#else\n    // For other platforms, you might want to handle it differently\n    std::cerr << "Unsupported operating system!" << std::endl;\n#endif\n}\n\nint main() {\n    clearConsole();\n    return 0;\n}