#include <iostream>\n#include <windows.h>\n#include <tlhelp32.h>\n#include <tchar.h>\n\nint CountRunningProcesses(const char *ProcessName) {\n    int count = 0;\n    PROCESSENTRY32 pe32 = { sizeof(PROCESSENTRY32) };\n    HANDLE hSnapshot = CreateToolhelp32Snapshot(TH32CS_SNAPPROCESS, 0);\n\n    if (hSnapshot != INVALID_HANDLE_VALUE) {\n        if (Process32First(hSnapshot, &pe32)) {\n            do {\n                if (_stricmp(pe32.szExeFile, ProcessName) == 0) {\n                    count++;\n                }\n            } while (Process32Next(hSnapshot, &pe32));\n        }\n        CloseHandle(hSnapshot);\n    }\n\n    return count;\n}\n\nint main() {\n    int notepadCount = CountRunningProcesses("notepad.exe");\n    std::cout << "Number of Notepad processes running: " << notepadCount << std::endl;\n    return 0;\n}