#include <vector>\n\nclass Matrix {\npublic:\n    explicit Matrix(size_t n) : data(n * n), size(n) {}\n\n    double& operator()(size_t i, size_t j) { return data[i * size + j]; }\n    const double& operator()(size_t i, size_t j) const { return data[i * size + j]; }\n\nprivate:\n    std::vector<double> data;\n    size_t size;\n};\n\nint main() {\n    Matrix matrix(1024);  // Create a 1024x1024 matrix\n    for (size_t i = 0; i < 1024; ++i) {\n        for (size_t j = 0; j < 1024; ++j) {\n            matrix(i, j) = static_cast<double>(i * j);  // Example operation\n        }\n    }\n    return 0;\n}