#include <windows.h>\n#include <comdef.h> // _COM_SMARTPTR_TYPEDEF macro and other useful functions\n#import "path\\to\\your\\dll.dll" no_namespace\n\nint main() {\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr)) {\n        printf("Could not initialize COM library!\n");\n        return 1;\n    }\n\n    // Create the COM object.\n    _MySimpleObjectPtr pSimpleObj(__uuidof(SimpleObject));\n    if (pSimpleObj == NULL) {\n        printf("Error during CoCreateInstance\n");\n        CoUninitialize();\n        return 2;\n    }\n\n    // Use the interface\n    pSimpleObj->SayHello();\n\n    // Uninitialize COM.\n    CoUninitialize();\n    return 0;\n}