#include <tchar.h>\n#include <windows.h>\n\n// Example function that changes from ASCII to Unicode support\nvoid SaveToFile(const TCHAR* filePath, const TCHAR* content) {\n    HANDLE hFile = CreateFile(filePath, GENERIC_WRITE, 0,\n                               NULL, CREATE_NEW, FILE_ATTRIBUTE_NORMAL, NULL);\n    if (hFile == INVALID_HANDLE_VALUE) {\n        // Handle error\n        return;\n    }\n\n    DWORD bytesWritten;\n    WriteFile(hFile, content, _tcslen(content) * sizeof(TCHAR), &bytesWritten, NULL);\n\n    CloseHandle(hFile);\n}\n\nint main() {\n    SaveToFile(_T("example.txt"), _T("Hello, world!"));\n    return 0;\n}