#include <iostream>\n#include <string>\n\nvoid generateSubstrings(const std::string& s, int index, std::string current) {\n    if (index == s.size()) {\n        if (!current.empty()) {\n            std::cout << current << std::endl;\n        }\n        return;\n    }\n\n    // Exclude the current character\n    generateSubstrings(s, index + 1, current);\n\n    // Include the current character\n    generateSubstrings(s, index + 1, current + s[index]);\n}\n\nint main() {\n    std::string input = "aaacdddghzz";\n    generateSubstrings(input, 0, "");\n    return 0;\n}