#include <yaml-cpp/yaml.h>\n#include <iostream>\n\nint main() {\n    // Load the YAML file\n    YAML::Node config = YAML::LoadFile("config.yaml");\n\n    // Access values by key\n    std::string name = config["name"].as<std::string>();\n    int age = config["age"].as<int>();\n\n    // Output the values\n    std::cout << "Name: " << name << "\n";\n    std::cout << "Age: " << age << "\n";\n\n    return 0;\n}