#include <windows.h>\n#include <iostream>\n\n#define CUSTOM_ASSERT(expression) \\n    if (!(expression)) {        \\n        if (IsDebuggerPresent())  \\n            DebugBreak();         \\n        else                      \\n            std::cerr << "Assertion failed: " #expression << std::endl; \\n    }\n\nvoid SomeFunction() {\n    int value = 0;\n    CUSTOM_ASSERT(value != 0); // Example assertion\n}\n\nint main() {\n    SomeFunction();\n    return 0;\n}