#include <iterator>\n#include <algorithm>\n#include <list>\n#include <iostream>\n\nint main(int argc, char **argv) {\n    std::list<int> a;\n    a.push_back(1);\n\n    std::list<std::list<int>> a_list;\n    a_list.push_back(a);\n\n    std::ostream_iterator<int> int_output(std::cout, "\n");\n\n    std::for_each(a_list.begin(), a_list.end(),\n        [&int_output](const std::list<int>& innerList) {\n            std::copy(innerList.begin(), innerList.end(), int_output);\n        }\n    );\n\n    return 0;\n}