#include <windows.h>\n#include <iostream>\n\nvoid safeDeleteFile(const std::wstring& filePath) {\n    if (!DeleteFileW(filePath.c_str())) {\n        DWORD error = GetLastError();\n        wcerr << L"Failed to delete file. Error code: " << error << endl;\n        // Handle the error appropriately\n    }\n}\n\nvoid safeCopyFile(const std::wstring& existingFilePath, const std::wstring& newFilePath) {\n    if (!CopyFileW(existingFilePath.c_str(), newFilePath.c_str(), FALSE)) {\n        DWORD error = GetLastError();\n        wcerr << L"Failed to copy file. Error code: " << error << endl;\n        // Handle the error appropriately\n    }\n}\n\nint main() {\n    std::wstring filePathToDelete = L"C:\\path\\to\\file.txt";\n    std::wstring sourceFilePath = L"C:\\path\\to\\source.txt";\n    std::wstring destinationFilePath = L"C:\\path\\to\\destination.txt";\n\n    safeDeleteFile(filePathToDelete);\n    safeCopyFile(sourceFilePath, destinationFilePath);\n\n    return 0;\n}