#include <cmath>\n#include <iostream>\n\nstruct Point2D {\n    double x;\n    double y;\n};\n\nPoint2D rotatePoint(const Point2D& point, double angleRad) {\n    Point2D rotatedPoint;\n    double cosA = std::cos(angleRad);\n    double sinA = std::sin(angleRad);\n\n    rotatedPoint.x = point.x * cosA - point.y * sinA;\n    rotatedPoint.y = point.x * sinA + point.y * cosA;\n\n    return rotatedPoint;\n}\n\nint main() {\n    Point2D originalPoint = {1.0, 0.0};\n    double angleDegrees = 90.0; // Rotate by 90 degrees\n    double angleRad = angleDegrees * (M_PI / 180.0);\n\n    Point2D rotatedPoint = rotatePoint(originalPoint, angleRad);\n\n    std::cout << "Original point: (" << originalPoint.x << ", " << originalPoint.y << ")\n";\n    std::cout << "Rotated point: (" << rotatedPoint.x << ", " << rotatedPoint.y << ")\n";\n\n    return 0;\n}